INSERT INTO BakeryRecipe.dbo.post (post_id, create_date, post_body, member_id) VALUES (1, N'2022-10-30 22:18:52.9960000', N'Bánh sữa tươi chiên khiến bao thực khách mê mẩn bởi phần nhân thơm phức, béo ngậy được bao bọc bởi lớp vỏ bánh giòn tan hấp dẫn khó cưỡng. Khi thưởng thức một miếng bánh bạn sẽ nhanh chóng bị hương vị hấp dẫn của món bánh này chinh phục. Đặc biệt, với thành phần chính là trứng gà, bánh sữa tươi chiên giòn còn cực kỳ tốt cho sức khỏe, rất thích hợp với những gia đình có trẻ em và người lớn tuổi.', 1);
